mapper.select:
SELECT * FROM SOCIAL s JOIN SOCIALMEMBER m ON s.socialNum = m.socialNum JOIN USERTABLE u ON m.ID = u.EMAIL JOIN CLUBDETAIL d ON d.SOCIALNUM = s.SOCIALNUM WHERE m.AUTH ='M' AND s.SOCIALCLUB = 'C'
AND s.CATEGORY = #{category}
ORDER BY s.SOCIALNUM DESC

mapper.update:
UPDATE SOCIAL 
   SET CATEGORY =#{category}
      ,TITLE = #{title}
      ,CONTENTS = #{contents}
      ,MAXIMUM = #{maximum}
 WHERE socialNum = #{socialNum}

UPDATE SOCIALMEMBER
   SET AUTH ='M'
 WHERE socialNum = #{socialNum}
   AND ID = #{id}

mapper.delete:
DELETE SOCIALMEMBER
 WHERE socialNum = #{socialNum}
   AND ID = #{id}

mapper.select:
SELECT SOCIALNUM
  FROM SOCIALMEMBER
 WHERE ID =#{id}
   AND socialNum = #{socialNum}

	SELECT *
      FROM CLUBBOARD b
	WHERE b.boardNum =#{boardNum}

mapper.update:
UPDATE CLUBBOARD
   SET TITLE =#{title}
      ,CONTENTS=#{contents}
      ,FILENAME=#{fileName}
      ,REWRITEDATE =SYSDATE
 WHERE boardNum =#{boardNum}
