mapper.update:
UPDATE SOCIAL 
   SET CATEGORY =#{category}
      ,TITLE = #{title}
      ,CONTENTS = #{contents}
      ,MAXIMUM = #{maximum}
 WHERE socialNum = #{socialNum}

UPDATE SOCIALMEMBER
   SET AUTH ='M'
 WHERE socialNum = #{socialNum}
   AND ID = #{id}

mapper.delete:
DELETE SOCIALMEMBER
 WHERE socialNum = #{socialNum}
   AND ID = #{id}

mapper.select:
SELECT SOCIALNUM
  FROM SOCIALMEMBER
 WHERE ID =#{id}
   AND socialNum = #{socialNum}
