mapper.select:
SELECT *
  FROM USERTABLE u
LEFT JOIN ADMIN d
    ON u.EMAIL = d.ID
 WHERE u.EMAIL = #{email}
   AND u.PASSWORD = #{password}

SELECT EMAIL
  FROM USERTABLE
 WHERE email = #{id}

SELECT EMAIL
  FROM USERTABLE
 WHERE NAME = #{name}
   AND PHONE = #{phone}
   AND BIRTH = #{birth}

SELECT count(*) 
  FROM USERTABLE
 WHERE EMAIL = #{email}

SELECT count(*) 
  FROM USERTABLE
 WHERE EMAIL = #{email}
   AND BIRTH = #{birth}
   AND NAME = #{name}

mapper.update:
UPDATE USERDETAIL
   SET DATELOG = SYSDATE
 WHERE id=#{email}

mapper.select:
SELECT count(*) 
  FROM USERTABLE
 WHERE NICKNAME = #{nickName}

mapper.update:
UPDATE USERTABLE
   SET NICKNAME = #{nickName}
 WHERE email=#{email}

mapper.select:
SELECT count(*)
  FROM USERTABLE
 WHERE NICKNAME = #{nickName}
   AND EMAIL = #{email}
