mapper.select:
SELECT *
  FROM USERTABLE
 WHERE EMAIL = #{email}
   AND PASSWORD = #{password}

SELECT EMAIL
  FROM USERTABLE
 WHERE email = #{id}

SELECT EMAIL
  FROM USERTABLE
 WHERE NAME = #{name}
   AND PHONE = #{phone}

SELECT count(*) 
  FROM USERTABLE
 WHERE EMAIL = #{email}

mapper.update:
UPDATE USERTABLE
   SET DATELOG = SYSDATE
 WHERE email=#{email}

mapper.select:
SELECT count(*) 
  FROM USERTABLE
 WHERE NICKNAME = #{nickName}

mapper.update:
UPDATE USERTABLE
   SET NICKNAME = #{nickName}
 WHERE email=#{email}
